/END-FREE * P Logger_getName... P E * *========================================================================= * Adds an appender to the specified logger. *========================================================================= * Parameters: * i_hLogger - Handle of the logger as it was returned by * Logger_getLogger() or Logger_getRootLogger(). * i_appName - The name of the appender. * * Returns: * void *========================================================================= P Logger_addAppender... P B export * D Logger_addAppender... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) D i_pAppender const like(LOG4RPG_pAppender_t ) * * Helper fields D appName S like(LOG4RPG_appenderName_t ) inz * * Logger handle D logger DS likeds(LOG4RPG_logger_t) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); return; endif;
// Return to caller if logger is NULL if Logger_isNull(i_hLogger); return; endif;
if Appender_isNull(i_pAppender); LogLog_warning('Appender is NULL. Please check call from [' + f_caller() + '] to [' + f_this() + '].'); return; endif;
appName = Appender_getName(i_pAppender);
if AppenderList_isNull(logger.pAppenderList); logger.pAppenderList = AppenderList_new(); endif;
if Appender_isNull(i_pAppender); LogLog_warning('Appender [' + appName + '] does not exist in repository.'); return; endif;
if AppenderList_put(logger.pAppenderList: i_pAppender) = 0; LogLog_debug('Appender [' + appName + '] added to logger [' + logger.name + '].'); else; LogLog_error('Failed to add appender [' + appName + '] to logger [' + logger.name + '].'); endif;
return;
/END-FREE * P Logger_addAppender... P E * *========================================================================= * Removes the specified appender from a given logger. *========================================================================= * Parameters: * i_hLogger - Handle of the logger as it was returned by * Logger_getLogger() or Logger_getRootLogger(). * i_appName - Name of the appender that is removed from the logger. * * Returns: * void *========================================================================= P Logger_removeAppender... P B export * D Logger_removeAppender... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) D i_appName const like(LOG4RPG_appenderName_t ) * * Logger handle D logger DS likeds(LOG4RPG_logger_t) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); if not Repository_isShutdown(); return; endif; endif;
// Return to caller if logger is NULL if Logger_isNull(i_hLogger); return; endif;
if AppenderList_isNull(logger.pAppenderList); return; endif;
AppenderList_remove(logger.pAppenderList: i_appName);
LogLog_verbose('Appender [' + i_appName + '] removed from logger [' + logger.name + '].');
return;
/END-FREE * P Logger_removeAppender... P E * *========================================================================= * Removes all appenders from the specified logger. *========================================================================= * Parameters: * i_hLogger - Handle of the logger as it was returned by * Logger_getLogger() or Logger_getRootLogger(). * * Returns: * void *========================================================================= P Logger_removeAllAppenders... P B export * D Logger_removeAllAppenders... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) * * Helper fields D appIter DS likeds(LOG4RPG_Appender... D ListIterator_t ) inz D pAppender S like(LOG4RPG_pAppender_t ) inz * * Logger handle D logger DS likeds(LOG4RPG_logger_t) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); return; endif;
// Return to caller if logger is NULL if Logger_isNull(i_hLogger); return; endif;
if AppenderList_isNull(logger.pAppenderList); return; endif;
appIter = AppenderList_getIterator(logger.pAppenderList);
dow AppenderList_getNumE(logger.pAppenderList) > 0; pAppender = AppenderList_getFirst(appIter); Logger_removeAppender(i_hLogger: Appender_getName(pAppender)); enddo;
return;
/END-FREE * P Logger_removeAllAppenders... P E * *========================================================================= * Tests the specified logger handle for a NULL value. *========================================================================= * Parameters: * i_hLogger - Handle of the logger as it was returned by * Logger_getLogger() or Logger_getRootLogger(). * * Returns: * isNull - Returns TRUE if the logger handle is NULL, else TRUE. *========================================================================= P Logger_isNull... P B export D PI N D i_hLogger const like(LOG4RPG_hLogger_t ) * * Logger handle D logger DS likeds(LOG4RPG_logger_t ) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
p_assert(i_hLogger = *NULL or logger.node = cLOG4RPG_NODE_LOGGER : 'Expected a logger node');
return Node_isNull(i_hLogger);
/END-FREE P E * *========================================================================= * Returns a logger NULL handle. *========================================================================= * Parameters: * void * * Returns: * hLogger - Returns a NULL handle. *========================================================================= P Logger_null... P B export D PI like(LOG4RPG_hLogger_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return *NULL;
/END-FREE P E * *========================================================================= * Sets the logging level of the specified logger. *========================================================================= * Parameters: * i_hLogger - Handle of the logger as it was returned by * Logger_getLogger() or Logger_getRootLogger(). * i_level - Logging level. Can be one of these value: * cLOG4RPG_LEVEL_DEBUG * cLOG4RPG_LEVEL_INFO * cLOG4RPG_LEVEL_WARN * cLOG4RPG_LEVEL_ERROR * cLOG4RPG_LEVEL_FATAL * * Returns: * void *========================================================================= P Logger_setLevel... P B export * D Logger_setLevel... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) D i_level const like(LOG4RPG_level_t ) * * Logger handle D logger DS likeds(LOG4RPG_logger_t ) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); return; endif;
// Return to caller if logger is NULL if Logger_isNull(i_hLogger); return; endif;
logger.priority = Level_toInt(f_ucase(i_level));
return;
/END-FREE * P Logger_setLevel... P E * *========================================================================= * Resets the logging level of the specified logger to use the * log level of the parent logger. *========================================================================= * Parameters: * i_hLogger - Handle of the logger as it was returned by * Logger_getLogger() or Logger_getRootLogger(). * * Returns: * void *========================================================================= P Logger_resetLevel... P B export * D Logger_resetLevel... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) * * Logger handle D logger DS likeds(LOG4RPG_logger_t ) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
