// Return to caller if logger is NULL if Logger_isNull(i_hLogger); return; endif;
logger.priority = cLOG4RPG_LEVEL_PARENT;
return;
/END-FREE * P Logger_resetLevel... P E * *========================================================================= * Allows to enable/disable log4rpg internal logging. *========================================================================= * Parameters: * i_debugMode - Specifies the debug mode for the log4rpg * internal logging. * i_output - Specifies the output target for the log events. * * Returns: * void * * Remark: This setting affects the complete LOG4RPG system. *========================================================================= P Logger_setInternalDebugging... P B export * D Logger_setInternalDebugging... D PI D i_debugMode const like(LOG4RPG_debugMode_t ) D i_output const like(LOG4RPG_debugOutput_t) D options(*nopass) * * Parameter positions D p_output C const(2) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); return; endif;
if %parms() >= p_output; LogLog_setInternalDebugging(i_debugMode: i_output); else; LogLog_setInternalDebugging(i_debugMode); endif;
return;
/END-FREE * P Logger_setInternalDebugging... P E * *========================================================================= * Returns the internal debug mode. *========================================================================= * Parameters: * void * * Returns: * debugMode - Mode of the internal logging. *========================================================================= P Logger_getInternalDebugMode... P B export * D Logger_getInternalDebugMode... D PI like(LOG4RPG_debugMode_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); return ''; endif;
return LogLog_getInternalDebugMode();
/END-FREE * P Logger_getInternalDebugMode... P E * *========================================================================= * Returns the target for internal log events. *========================================================================= * Parameters: * void * * Returns: * output - Output target for internal log events. *========================================================================= P Logger_getInternalDebugOutput... P B export * D Logger_getInternalDebugOutput... D PI like(LOG4RPG_debugOutput_t) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
