hProvNode = i_hProvNode; hLogger = i_hLogger;
p_assert(provNode.node = cLOG4RPG_NODE_PROVISION : 'Expected a provision node'); p_assert(logger.node = cLOG4RPG_NODE_LOGGER : 'Expected a logger node'); p_assert(BasicList_getNumE(logger.pChildren) = 0: 'Logger must not yet have any hildren');
numChildren = BasicList_getNumE(provNode.pChildren); for x = numChildren downto 1; hChild = BasicList_getPointer(provNode.pChildren: x); ProvisionNode_removeChild(i_hProvNode: x); Logger_addChild(i_hLogger: hChild); endfor;
p_assert(BasicList_getNumE(provNode.pChildren) = 0: 'Provision node must not have any hildren'); p_assert(BasicList_getNumE(logger.pChildren) = numChildren: 'Logger must have ' + %char(numChildren) + ' any children');
/END-FREE P E * *========================================================================= * Tests the specified provision node handle for a NULL value. *========================================================================= * Parameters: * i_hProvNode - Handle of the provision node. * * Returns: * isNull - Returns TRUE if the handle is NULL, else TRUE. *========================================================================= P ProvisionNode_isNull... P B export D PI N D i_hProvNode const like(LOG4RPG_hLogger_t ) * * Logger handle D provNode DS likeds(LOG4RPG_logger_t ) D based(i_hProvNode) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
p_assert(i_hProvNode = *NULL or provNode.node = cLOG4RPG_NODE_PROVISION : 'Expected a provision node');
return Node_isNull(i_hProvNode);
/END-FREE P E * *========================================================================= * Returns a provision node NULL handle. *========================================================================= * Parameters: * void * * Returns: * hProvNode - Returns a NULL handle. *========================================================================= P ProvisionNode_null... P B export D PI like(LOG4RPG_hLogger_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return Node_null();
/END-FREE P E * *========================================================================= R * *** Private *** * Clears a given logger handle. *========================================================================= P clearHandleData... P B * D clearHandleData... D PI D io_logger likeds(LOG4RPG_logger_t) * * Return value * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clear io_logger;
return;
/END-FREE * P clearHandleData... P E * *========================================================================= R * *** Private *** * Deletes a logger. *========================================================================= P deleteLogger... P B * D deleteLogger... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) * * Helper fields D logName S like(LOG4RPG_loggerName_t ) inz D appIter DS likeds(LOG4RPG_Appender... D ListIterator_t ) inz D pAppender S like(LOG4RPG_pAppender_t ) inz D logger DS likeds(LOG4RPG_logger_t ) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
logName = logger.name;
if logger.pAppenderList <> AppenderList_null(); appIter = AppenderList_getIterator(logger.pAppenderList);
dow AppenderList_getNumE(logger.pAppenderList) > 0; pAppender = AppenderList_getFirst(appIter); Logger_removeAppender(i_hLogger: Appender_getName(pAppender)); enddo;
AppenderList_delete(logger.pAppenderList); endif;
clearHandleData(logger);
return;
/END-FREE * P deleteLogger... P E * *========================================================================= R * *** Private *** * Returns cTrue if logging is enabled. *========================================================================= P isLoggingEnabled... P B * D isLoggingEnabled... D PI N D i_hLogger const like(LOG4RPG_hLogger_t ) * * Logger handle D logger DS likeds(LOG4RPG_logger_t) D based(i_hLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Return to caller if logging is disabled. if not Logger_isLogging(); return cFalse; endif;
// Return to caller if logger is NULL if Logger_isNull(i_hLogger); return cFalse; endif;
// Reload properties (if reloading has been enabled) Configurator_checkAndReloadPropertiesConfiguration();
// Return to caller if logger does not exist if not Repository_existLogger(logger.name); return cFalse; endif;
return cTrue;
/END-FREE * P isLoggingEnabled... P E * *========================================================================= R * *** Private *** * Returns the logging priority from the logger hierarchy. *========================================================================= P getPriorityFromHierarchy... P B * D getPriorityFromHierarchy... D PI like(LOG4RPG_priority_t ) D i_hLogger const like(LOG4RPG_hLogger_t ) * * Helper fields D logger DS likeds(LOG4RPG_logger_t ) D based(pLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
pLogger = i_hLogger;
dow logger.priority = cLOG4RPG_LEVEL_PARENT; if logger.pParent = Logger_null(); return cLOG4RPG_PRIORITY_NONE; leave; endif; pLogger = logger.pParent; enddo;
return logger.priority;
/END-FREE * P getPriorityFromHierarchy... P E * *========================================================================= R * *** Private *** * Logs the logging event. *========================================================================= P logLoggingEvent... P B * D logLoggingEvent... D PI D i_hLogger const like(LOG4RPG_hLogger_t ) D i_priority const like(LOG4RPG_priority_t ) D i_text const like(LOG4RPG_text_t ) D options(*varsize) D i_caller const likeds(LOG4RPG_caller_t ) D i_errMsg const likeds(msg_t ) D options(*nopass) * * Parameter positions D p_errMsg C 5 * * Helper fields D isMsgRtvErr S N inz(cFalse) D msgRtvErrText S 128A inz * D x S 10I 0 inz D pPropList S like(LOG4RPG_pPropertyList_t ) inz D pAppenderList S like(LOG4RPG_pAppenderList_t ) inz D pAppender S like(LOG4RPG_pAppender_t ) inz D pLogEvent S like(LOG4RPG_pLogEvent_t ) inz * D appIter DS likeds(LOG4RPG_Appender... D ListIterator_t ) inz * * Logger handle D logger DS likeds(LOG4RPG_logger_t ) D based(i_hLogger) * * Structure for message data of CPF2419 D CPF2419 DS qualified D msgID 7A D msgF 10A D msgFLib 10A * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
pAppenderList = getAppenderListFromHierarchy(i_hLogger); if AppenderList_isNull(pAppenderList); if (logger.name = cROOT_LOGGER) and not g_warnRootNoAppender; LogLog_warning('No appenders could be found for logger [root]'); LogLog_warning('Please initialize the log4rpg system properly.'); g_warnRootNoAppender = cTrue; endif; return; endif;
appIter = AppenderList_getIterator(pAppenderList); pAppender = AppenderList_getFirst(appIter);
pLogEvent = LogEvent_new(logger.name : i_priority : '' : i_caller);
if (%parms() >= p_errMsg); if (i_errMsg.ID = 'CPF2419'); CPF2419 = i_errMsg.data; msgRtvErrText = 'Unable to retrieve message description of + message ' + CPF2419.msgID + ' from message file ' + %trim(CPF2419.msgFLib) + '/' + %trim(CPF2419.msgF); LogLog_error(msgRtvErrText); isMsgRtvErr = cTrue; endif; endif;
dow not Appender_isNull(pAppender);
if (isMsgRtvErr); LogEvent_setText(pLogEvent: msgRtvErrText); else; LogEvent_setText(pLogEvent: i_text); endif;
Appender_doAppend(pAppender: pLogEvent); pAppender = AppenderList_getNext(appIter);
pPropList = LogEvent_getPropertyList(pLogEvent); if not PropertyList_isNull(pPropList); PropertyList_clear(pPropList); LogLog_verbose('> Cleared property list of log event'); endif;
enddo;
LogEvent_delete(pLogEvent);
return;
/END-FREE * P logLoggingEvent... P E * *========================================================================= R * *** Private *** * Returns the appender list from the logger hierarchy. *========================================================================= P getAppenderListFromHierarchy... P B * D getAppenderListFromHierarchy... D PI like(LOG4RPG_pAppenderList_t ) D i_hLogger const like(LOG4RPG_hLogger_t ) * * Helper fields D logger DS likeds(LOG4RPG_logger_t ) D based(pLogger) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
pLogger = i_hLogger;
dow logger.pAppenderList = AppenderList_null(); if logger.pParent = Logger_null(); return AppenderList_null(); leave; endif; pLogger = logger.pParent; enddo;
return logger.pAppenderList;
/END-FREE * P getAppenderListFromHierarchy... P E * *========================================================================= R * *** private *** * Initializes a given logger handle. *========================================================================= * Parameters: * i_logger - Logger handle. * * Returns: * void *========================================================================= P initializeLoggerHandle... P B * D initializeLoggerHandle... D PI D io_logger likeds(LOG4RPG_logger_t ) D i_logName const like(LOG4RPG_loggerName_t) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clearHandleData(io_logger);
io_logger.node = cLOG4RPG_NODE_LOGGER; // Logger node io_logger.pParent = Logger_null(); // Parent logger io_logger.name = i_logName; // Logger name io_logger.priority = cLOG4RPG_LEVEL_PARENT; // Use log level of parent logger io_logger.pAppenderList= AppenderList_null(); // Appender list io_logger.pChildren = BasicList_new('*CHILDREN');// Children of logger node
if i_logName = cROOT_LOGGER; io_logger.priority = Level_toInt(cLOG4RPG_LEVEL_DEBUG); endif;
return;
/END-FREE * P initializeLoggerHandle... P E * *========================================================================= R * *** private *** * Initializes a given provision node handle. *========================================================================= P initializeProvisionNodeHandle... P B * D initializeProvisionNodeHandle... D PI D io_provNode likeds(LOG4RPG_logger_t ) D i_nodeName const like(LOG4RPG_loggerName_t) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clearHandleData(io_provNode);
io_provNode.node = cLOG4RPG_NODE_PROVISION; // Provision node io_provNode.pParent = Logger_null(); // No parent logger io_provNode.name = i_nodeName; // Node name io_provNode.priority = cLOG4RPG_LEVEL_PARENT; // Use log level of parent logg io_provNode.pAppenderList= AppenderList_null(); // No Appender list io_provNode.pChildren = BasicList_new('*CHILDREN');// Children of provision node
return;
/END-FREE * P initializeProvisionNodeHandle... P E * *========================================================================= R * *** private *** * Sets an offset to let f_caller() skip the Log4rpg stub module * if present. *========================================================================= P setStubOfs... P B export * D setStubOfs... D PI D i_stubOfs 3I 0 const * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
g_stubOfs = i_stubOfs;
return;
/END-FREE * P setStubOfs... P E * *========================================================================= R * *** private *** * Initializes this module. *========================================================================= P initializeModule... P B * D initializeModule... D PI * * Return value * * Helper fields * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if g_isInit; return; endif;
Loglog_verbose('Initializing module ' + sds.modContProc + '...');
Repository_initialize();
if not Repository_existLogger(cROOT_LOGGER); // Configurator_loadDefaultConfiguration(); endif;
g_isInit = cTrue;
return;
/END-FREE * P initializeModule... P E *

*=====================================================================* * SrvPgm: LOG4RPG - Object: LoggerList * *=====================================================================* * Author : Thomas Raddatz * * Date : 13.06.2006 * * E-mail : thomas.raddatz@tools400.de * * Homepage: www.tools400.de * *=====================================================================* * * * This software is free software, you can redistribute it and/or * * modify it under the terms of the GNU General Public License (GPL) * * as published by the Free Software Foundation. * * * * See GNU General Public License for details. * * http://www.opensource.org * * http://www.opensource.org/licenses/gpl-license.html * * * *=====================================================================* * History: * * * * Datum Name nderung * * ---------- ------------ --------------------------------------- * * * *=====================================================================* * >>PRE-COMPILER<< * * * * >>CRTCMD<< CRTRPGMOD MODULE(&LI/&OB) + * * SRCFILE(&SL/&SF) + * * SRCMBR(&SM); * * * * >>COMPILE<< * * >>PARM<< TRUNCNBR(*NO); * * >>PARM<< DBGVIEW(*LIST); * * >>PARM<< TGTRLS(V6R1M0 ); * * >>END-COMPILE<< * * * * >>EXECUTE<< * * * * >>END-PRE-COMPILER<< * *=====================================================================* /COPY QLOG4RPG,H_SPEC *=====================================================================* * * ----------------------------------------- * Exported functions * ----------------------------------------- /COPY QLOG4RPG,PLOG4RPG02 * * ----------------------------------------- * Type definitions * ----------------------------------------- /COPY QLOG4RPG,PTYPES Private type definitions * D handle_t... D DS qualified based(pDummy) D pMap like(BasicMap_handle_t ) * * ----------------------------------------- * Prototypes * ----------------------------------------- /COPY QBASICS1,PBASICS1 Basic RPG Extensions /COPY QIFS,PIFS IFS Stream file support * /COPY QLOG4RPG,PLOG4RPG10 LogLog /COPY QLOG4RPG,PLOG4RPG99 Helpers * * ----------------------------------------- * Private prototypes * ----------------------------------------- * * ----------------------------------------- * Global constants * ----------------------------------------- * * ----------------------------------------- * Global fields * ----------------------------------------- * *========================================================================= * Creates a new logger list instance. *========================================================================= * Parameters: * void * * Returns: * pLoggerList - Returns a handle to the logger list on success, else * a NULL handle. *========================================================================= P LoggerList_new... P B export * D LoggerList_new... D PI like(LOG4RPG_pLoggerList_t ) * * Return value D logList DS likeds(handle_t ) D based(pLogList) * * Helper fields D tempLogList DS likeds(handle_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
LogLog_verbose('Creating logger list ...');
pLogList = LoggerList_null();
// Initialize appender list handle clear tempLogList; tempLogList.pMap = BasicMap_new();
// Return properties list to caller pLogList = %alloc(%size(handle_t)); logList = tempLogList;
LogLog_verbose('+ Allocated memory for logger list.');
return pLogList;
/END-FREE * P LoggerList_new... P E * *========================================================================= * Returns the number of entries in the specified logger list. *========================================================================= * Parameters: * i_pLogList - Logger list returned by LoggerList_new(). * * Returns: * numE - Returns the number of loggers in this * logger list on success, else -1. *========================================================================= P LoggerList_getNumE... P B export * D LoggerList_getNumE... D PI 10I 0 D i_pLogList const like(LOG4RPG_pLoggerList_t ) * * Logger list handle D logList DS likeds(handle_t ) D based(i_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return BasicMap_getNumE(logList.pMap);
/END-FREE * P LoggerList_getNumE... P E * *========================================================================= * Adds a logger to the specified logger list. *========================================================================= * Parameters: * i_pLogList - Logger list returned by LoggerList_new(). * i_pLogger - Logger that is added to this logger list. * * Returns: * index - Returns the index of the logger on success, * else -1. *========================================================================= P LoggerList_put... P B export * D LoggerList_put... D PI 10I 0 D i_pLogList const like(LOG4RPG_pLoggerList_t ) D i_pLogger const like(LOG4RPG_hLogger_t ) * * Helper fields D pLogger S like(i_pLogger) * * Logger list handle D logList DS likeds(handle_t ) D based(i_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
pLogger = i_pLogger;
return BasicMap_put(logList.pMap: Logger_getName(pLogger) : %addr(pLogger) : %len(pLogger ));
/END-FREE * P LoggerList_put... P E * *========================================================================= * Removes the specified logger from the given logger list. *========================================================================= * Parameters: * i_pLogList - Logger list returned by LoggerList_new(). * i_logName - Name of the logger that is removed from * this logger list. * * Returns: * rc - Returns 0 on success, else -1. *========================================================================= P LoggerList_remove... P B export * D LoggerList_remove... D PI 10I 0 D i_pLogList const like(LOG4RPG_pLoggerList_t ) D i_logName const like(LOG4RPG_loggerName_t ) * * Logger list handle D logList DS likeds(handle_t ) D based(i_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return BasicMap_remove(logList.pMap: i_logName);
/END-FREE * P LoggerList_remove... P E * *========================================================================= * Returns the logger assigned to the specified logger name. *========================================================================= * Parameters: * i_pLogList - Logger list returned by LoggerList_new(). * i_logName - Name of the logger that is returned. * * Returns: * pLogger - Returns the associated logger on ssuccess, else NULL. *========================================================================= P LoggerList_get... P B export * D LoggerList_get... D PI like(LOG4RPG_hLogger_t ) D i_pLogList const like(LOG4RPG_pLoggerList_t ) D i_logName const like(LOG4RPG_loggerName_t ) * * Return value D pLogger S like(LOG4RPG_hLogger_t ) inz * * Logger list handle D logList DS likeds(handle_t ) D based(i_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
BasicMap_get(logList.pMap: i_logName: %addr(pLogger) : %size(pLogger));
return pLogger;
/END-FREE * P LoggerList_get... P E * *========================================================================= * Tests, if the logger list contains the specified logger. *========================================================================= * Parameters: * i_pLogList - Logger list returned by LoggerList_new(). * i_logName - Name of the logger. * * Returns: * exist - Returns TRUE if the logger exists, else FALSE. *========================================================================= P LoggerList_exist... P B export * D LoggerList_exist... D PI N D i_pLogList const like(LOG4RPG_pLoggerList_t ) D i_logName const like(LOG4RPG_loggerName_t ) * * Logger list handle D logList DS likeds(handle_t ) D based(i_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return BasicMap_containsKey(logList.pMap: i_logName);
/END-FREE * P LoggerList_exist... P E * *========================================================================= * Returns an iterator for the specified logger list. *========================================================================= * Parameters: * i_pLogList - Logger list returned by LoggerList_new(). * * Returns: * logIter - Iterator for the logger list. *========================================================================= P LoggerList_getIterator... P B export * D LoggerList_getIterator... D PI likeds(LOG4RPG_Logger... D ListIterator_t) D i_pLogList const like(LOG4RPG_pLoggerList_t ) * * Return value D logIter DS likeds(LOG4RPG_Logger... D ListIterator_t) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clear logIter; logIter.pLogList = i_pLogList; logIter.lastLog = 0;
return logIter;
/END-FREE * P LoggerList_getIterator... P E * *========================================================================= * Returns the first logger of the specified logger list. *========================================================================= * Parameters: * i_logIter - Iteratur returned by LoggerList_getIterator(). * * Returns: * pLogger - Returns the first logger in the specified * logger list on success, else a NULL handle. *========================================================================= P LoggerList_getFirst... P B export * D LoggerList_getFirst... D PI like(LOG4RPG_hLogger_t ) D io_logIter likeds(LOG4RPG_Logger... D ListIterator_t) * * Return value D pLogger S like(LOG4RPG_hLogger_t ) inz * * Helper fields D logName S like(LOG4RPG_loggerName_t ) inz * * Logger list handle D logList DS likeds(handle_t ) D based(io_logIter.pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
pLogger = Logger_null();
if LoggerList_getNumE(io_logIter.pLogList) = 0; return pLogger; endif;
io_logIter.lastLog = 1;
logName = BasicMap_getKeyByIndex(logList.pMap : io_logIter.lastLog); pLogger = LoggerList_get(io_logIter.pLogList: logName);
return pLogger;
/END-FREE * P LoggerList_getFirst... P E * *========================================================================= * Returns the next logger of the specified logger list. *========================================================================= * Parameters: * i_logIter - Iteratur returned by LoggerList_getIterator(). * * Returns: * pLogger - Returns the next logger in the specified * logger list on success, else a NULL handle. *========================================================================= P LoggerList_getNext... P B export * D LoggerList_getNext... D PI like(LOG4RPG_hLogger_t ) D io_logIter likeds(LOG4RPG_Logger... D ListIterator_t) * * Return value D pLogger S like(LOG4RPG_hLogger_t ) inz * * Helper fields D logName S like(LOG4RPG_loggerName_t ) inz * * Logger list handle D logList DS likeds(handle_t ) D based(io_logIter.pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
pLogger = Logger_null();
if LoggerList_getNumE(io_logIter.pLogList) <= io_logIter.lastLog; return pLogger; endif;
io_logIter.lastLog = io_logIter.lastLog + 1;
logName = BasicMap_getKeyByIndex(logList.pMap : io_logIter.lastLog); pLogger = LoggerList_get(io_logIter.pLogList: logName);
return pLogger;
/END-FREE * P LoggerList_getNext... P E * *========================================================================= * Deletes the specified logger list. *========================================================================= * Parameters: * io_pLogList - Logger list returned by LoggerList_new(). * * Returns: * isDeleted - Returns TRUE on success, else FALSE. *========================================================================= P LoggerList_delete... P B export * D LoggerList_delete... D PI N D io_pLogList like(LOG4RPG_pLoggerList_t ) * * Return value D isDeleted S N inz(cFalse) * * Helper fields D logIter DS likeds(LOG4RPG_Logger... D ListIterator_t ) inz D pLogger S like(LOG4RPG_hLogger_t ) inz * * Logger list handle D logList DS likeds(handle_t ) D based(io_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if BasicMap_isNull(logList.pMap); return cTrue; endif;
LogLog_verbose('Deleting logger list ...');
p_assert(LoggerList_getNumE(io_pLogList) = 0 : 'All loggers should have been removed from this + logger list by the repository.');
logIter = LoggerList_getIterator(io_pLogList);
dow LoggerList_getNumE(io_pLogList) > 0; pLogger = LoggerList_getFirst(logIter); LoggerList_remove(io_pLogList: Logger_getName(pLogger)); Logger_delete(pLogger); enddo;
if BasicMap_delete(logList.pMap) = 0; isDeleted = cTrue; else; isDeleted = cFalse; endif;
io_pLogList = LoggerList_null();
LogLog_verbose('- Deleted Logger list');
return isDeleted;
/END-FREE * P LoggerList_delete... P E * *========================================================================= * Tests the specified logger list for a NULL value. *========================================================================= * Parameters: * i_logList - Logger list returned by LoggerList_new(). * * Returns: * isNull - Returns TRUE if the handle of the logger list is a * NULL handle. *========================================================================= P LoggerList_isNull... P B export * D LoggerList_isNull... D PI N D i_pLogList const like(LOG4RPG_pLoggerList_t ) * * Logger list handle D logList DS likeds(handle_t ) D based(i_pLogList) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if i_pLogList = LoggerList_null(); return cTrue; else; return BasicMap_isNull(logList.pMap); endif;
/END-FREE * P LoggerList_isNull... P E * *========================================================================= * Returns a NULL logger list. *========================================================================= * Parameters: * void * * Returns: * pLogList - Logger list NULL handle. *========================================================================= P LoggerList_null... P B export * D LoggerList_null... D PI like(LOG4RPG_pLoggerList_t ) * * Return value D logList S like(LOG4RPG_pLoggerList_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return BasicMap_null();
/END-FREE * P LoggerList_null... P E *

