select; when i_priority = cLOG4RPG_PRIORITY_DEBUG; return cLOG4RPG_LEVEL_DEBUG; when i_priority = cLOG4RPG_PRIORITY_INFO; return cLOG4RPG_LEVEL_INFO; when i_priority = cLOG4RPG_PRIORITY_WARN; return cLOG4RPG_LEVEL_WARN; when i_priority = cLOG4RPG_PRIORITY_ERROR; return cLOG4RPG_LEVEL_ERROR; when i_priority = cLOG4RPG_PRIORITY_FATAL; return cLOG4RPG_LEVEL_FATAL; other; return ''; endsl;
/END-FREE * P Level_toString... P E *

*=====================================================================* * SrvPgm: LOG4RPG - Object: LogLog * *=====================================================================* * Author : Thomas Raddatz * * Date : 13.06.2006 * * E-mail : thomas.raddatz@tools400.de * * Homepage: www.tools400.de * *=====================================================================* * * * This software is free software, you can redistribute it and/or * * modify it under the terms of the GNU General Public License (GPL) * * as published by the Free Software Foundation. * * * * See GNU General Public License for details. * * http://www.opensource.org * * http://www.opensource.org/licenses/gpl-license.html * * * *=====================================================================* * History: * * * * Datum Name nderung * * ---------- ------------ --------------------------------------- * * 27.10.2006 Th.Raddatz Added feature to completely enable/ * * disable Log4rpg. Logger_isLogging(). * * * * 15.11.2006 Th.Raddatz Added procedures: * * LogLog_getInternalDebugMode() * * LogLog_getInternalDebugOutput() * * * *=====================================================================* * >>PRE-COMPILER<< * * * * >>CRTCMD<< CRTRPGMOD MODULE(&LI/&OB) + * * SRCFILE(&SL/&SF) + * * SRCMBR(&SM); * * * * >>COMPILE<< * * >>PARM<< TRUNCNBR(*NO); * * >>PARM<< DBGVIEW(*LIST); * * >>PARM<< TGTRLS(V6R1M0 ); * * >>END-COMPILE<< * * * * >>EXECUTE<< * * * * >>END-PRE-COMPILER<< * *=====================================================================* /COPY QLOG4RPG,H_SPEC *=====================================================================* FLOG4RPG O F 132 PRINTER usropn * * ----------------------------------------- * Exported functions * ----------------------------------------- /COPY QLOG4RPG,PLOG4RPG10 * * ----------------------------------------- * Type definitions * ----------------------------------------- /COPY QLOG4RPG,PLOG4RPG Public interface & type definitions * * ----------------------------------------- * Prototypes * ----------------------------------------- /COPY QLOG4RPG,CLIB C-Library /COPY QBASICS1,PBASICS1 Basic RPG Extensions /COPY QLOG4RPG,PLOG4RPG14 DebugMode /COPY QLOG4RPG,PLOG4RPG15 DebugOutput * * ----------------------------------------- * Private prototypes * ----------------------------------------- * * This method is used to output log4rpg internal * statements. D streamOut... D PR D i_text const like(LOG4RPG_formattedText_t) * * This method is used to output log4rpg internal * statements. Output goes to stdout. D writeToStdout... D PR D i_text const like(LOG4RPG_formattedText_t) * * This method is used to output log4rpg internal * statements. Output goes to the printer. D writeToPrinter... D PR D i_text const like(LOG4RPG_formattedText_t) * * Formats the name of the calling procedure. D formatCaller... D PR 256A varying D i_procName 256A const varying * * ----------------------------------------- * Global constants * ----------------------------------------- D cPREFIX... D C const('log4rpg: ') D cERR_PREFIX... D C const('log4rpg:ERROR ') D cWARN_PREFIX... D C const('log4rpg:WARN ') * * Constants for printf() D cESC_NEW_LINE C const(x'15') D cESC_TAB C const(x'05') D cESC_BACKSLASH C const(x'EC') * * ----------------------------------------- * Global fields * ----------------------------------------- D g_debugMode S 5U 0 inz( D cLOG4RPG_INT_DEBUG_OFF) D g_output S 5U 0 inz( D cLOG4RPG_INT_DEBUG_OUTPUT_PRINTER) * D g_printText S 117A * * ----------------------------------------- * Output to printer * ----------------------------------------- OLOG4RPG E printLine O g_printText 127 * *========================================================================= * Allows to enable/disable log4rpg internal logging. *========================================================================= * Parameters: * i_debugMode - Specifies the debug mode for the log4rpg * internal logging. * * Returns: * void *========================================================================= P LogLog_setInternalDebugging... P B export * D LogLog_setInternalDebugging... D PI D i_debugMode const like(LOG4RPG_debugMode_t ) D i_output const like(LOG4RPG_debugOutput_t) D options(*nopass) * * Parameter positions D p_output C const(2) * * Fields for optional parameters D output S like(i_output ) inz * * Helper fields D mode S like(i_debugMode ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if %parms() >= p_output; output = f_lcase(i_output); else; output = cLOG4RPG_DEBUG_OUTPUT_PRINTER; endif;
mode = f_lcase(i_debugMode);
if DebugMode_isValid(mode); g_debugMode = DebugMode_toInt(mode); else; g_debugMode = DebugMode_toInt(cLOG4RPG_DEBUG_ON); LogLog_error('Unexpected value [' + mode + '] passed for parameter ''debug mode'' to + procedure [' + f_this + ']'); endif;
if DebugOutput_isValid(output); g_output = DebugOutput_toInt(output); else; g_output = cLOG4RPG_INT_DEBUG_OUTPUT_PRINTER; LogLog_error('Unexpected value [' + output + '] passed for parameter ''output'' to + procedure [' + f_this + ']'); endif;
if (g_output <> cLOG4RPG_INT_DEBUG_OUTPUT_PRINTER) and %open(LOG4RPG); close LOG4RPG; endif;
return;
/END-FREE * P LogLog_setInternalDebugging... P E * *========================================================================= * Returns the internal debug mode. *========================================================================= * Parameters: * void * * Returns: * debugMode - Mode of the internal logging. *========================================================================= P LogLog_getInternalDebugMode... P B export * D LogLog_getInternalDebugMode... D PI like(LOG4RPG_debugMode_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return DebugMode_toString(g_debugMode);
/END-FREE * P LogLog_getInternalDebugMode... P E * *========================================================================= * Returns the target for internal log events. *========================================================================= * Parameters: * void * * Returns: * output - Output target for internal log events. *========================================================================= P LogLog_getInternalDebugOutput... P B export * D LogLog_getInternalDebugOutput... D PI like(LOG4RPG_debugOutput_t) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return DebugOutput_toString(g_output);
/END-FREE * P LogLog_getInternalDebugOutput... P E * *========================================================================= * This method is used to output log4rpg internal debug statements. *========================================================================= * Parameters: * i_text - The debug statement. * * Returns: * void *========================================================================= P LogLog_debug... P B export * D LogLog_debug... D PI D i_text const like(LOG4RPG_text_t ) D caller S 128a varying * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
if g_debugMode <> cLOG4RPG_INT_DEBUG_OFF; streamOut(cPREFIX + formatCaller(f_caller()) + i_text); endif;
return;
/END-FREE * P LogLog_debug... P E * *========================================================================= * This method is used to output log4rpg internal debug messages. *========================================================================= * Parameters: * i_msg - The debug message. * * Returns: * void *========================================================================= P LogLog_debugMsg... P B export * D LogLog_debugMsg... D PI D i_msg const likeds(msg_t ) * * Helper fields D text S like(LOG4RPG_text_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
if g_debugMode <> cLOG4RPG_INT_DEBUG_OFF; text = %trimR(f_rtvMsgText(i_msg: *omit)); LogLog_debug(text); endif;
return;
/END-FREE * P LogLog_debugMsg... P E * *========================================================================= * This method is used to output log4rpg internal error statements. *========================================================================= * Parameters: * i_text - The error statement. * * Returns: * void *========================================================================= P LogLog_error... P B export * D LogLog_error... D PI D i_text const like(LOG4RPG_text_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
streamOut(cERR_PREFIX + formatCaller(f_caller()) + i_text);
return;
/END-FREE * P LogLog_error... P E * *========================================================================= * This method is used to output log4rpg internal error messages. *========================================================================= * Parameters: * i_msg - The error message. * * Returns: * void *========================================================================= P LogLog_errorMsg... P B export * D LogLog_errorMsg... D PI D i_msg const likeds(msg_t ) * * Helper fields D text S like(LOG4RPG_text_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
text = %trimR(f_rtvMsgText(i_msg: *omit)); LogLog_error(text);
return;
/END-FREE * P LogLog_errorMsg... P E * *========================================================================= * This method is used to output log4rpg internal warning statements. *========================================================================= * Parameters: * i_text - The warning statement. * * Returns: * void *========================================================================= P LogLog_warning... P B export * D LogLog_warning... D PI D i_text const like(LOG4RPG_text_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
streamOut(cWARN_PREFIX + formatCaller(f_caller()) + i_text);
return;
/END-FREE * P LogLog_warning... P E * *========================================================================= * This method is used to output log4rpg internal warning message. *========================================================================= * Parameters: * i_msg - The warning message. * * Returns: * void *========================================================================= P LogLog_warningMsg... P B export * D LogLog_warningMsg... D PI D i_msg const likeds(msg_t ) * * Helper fields D text S like(LOG4RPG_text_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
text = %trimR(f_rtvMsgText(i_msg: *omit)); LogLog_warning(text);
return;
/END-FREE * P LogLog_warningMsg... P E * *========================================================================= * This method is used to output log4rpg internal verbose statements. *========================================================================= * Parameters: * i_text - The verbose statement. * * Returns: * void *========================================================================= P LogLog_verbose... P B export * D LogLog_verbose... D PI D i_text const like(LOG4RPG_text_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_VERBOSE; streamOut(cPREFIX + formatCaller(f_caller()) + i_text); endif;
return;
/END-FREE * P LogLog_verbose... P E * *========================================================================= * This method is used to output log4rpg internal verbose message. *========================================================================= * Parameters: * i_msg - The verbose message. * * Returns: * void *========================================================================= P LogLog_verboseMsg... P B export * D LogLog_verboseMsg... D PI D i_msg const likeds(msg_t ) * * Helper fields D text S like(LOG4RPG_text_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if not Logger_isLogging(); return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_QUIET; return; endif;
if g_debugMode = cLOG4RPG_INT_DEBUG_VERBOSE; text = %trimR(f_rtvMsgText(i_msg: *omit)); LogLog_verbose(text); endif;
return;
/END-FREE * P LogLog_verboseMsg... P E * *========================================================================= R * *** Private *** * This method is used write log4rpg internal statements to the output. *========================================================================= P streamOut... P B * D streamOut... D PI D i_text const like(LOG4RPG_formattedText_t) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
select; when g_output = cLOG4RPG_INT_DEBUG_OUTPUT_PRINTER; writeToPrinter(i_text); when g_output = cLOG4RPG_INT_DEBUG_OUTPUT_STDOUT; writeToStdout(i_text); endsl;
return;
/END-FREE * P streamOut... P E * *========================================================================= R * *** Private *** * This method is used to output log4rpg internal statements. * Output goes to printer. *========================================================================= P writeToPrinter... P B * D writeToPrinter... D PI D i_text const like(LOG4RPG_formattedText_t) * * Helper fields D startPos S 10U 0 inz(1) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if i_text = ''; return; endif;
if not %open(LOG4RPG); system('OVRPRTF FILE(LOG4RPG) + TOFILE(QSYS/QSYSPRT) OVRSCOPE(*CALLLVL)'); open LOG4RPG; endif;
dow startPos > 0; g_printText = f_wordWrap(i_text : %len(g_printText) : startPos : cWORDWRAP_PARAGRAPH_TEXT); except printLine; enddo;
return;
/END-FREE * P writeToPrinter... P E * *========================================================================= R * *** Private *** * This method is used to output log4rpg internal statements. * Output goes to stdout. *========================================================================= P writeToStdout... P B * D writeToStdout... D PI D i_text const like(LOG4RPG_formattedText_t) * * printf -- Print Formatted Characters D printf... D PR 10I 0 extproc('printf') D i_fmtString * value options(*string) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
printf(i_text + cESC_NEW_LINE);
return;
/END-FREE * P writeToStdout... P E * *========================================================================= R * *** Private *** * Formats the name of the calling procedure. *========================================================================= P formatCaller... P B D PI 256A varying D i_procName 256A const varying * D formatted S 256A varying D partLen S 10I 0 * D DELIMITERS C '....' D LENGTH C 40 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if (%len(i_procName) <= LENGTH); formatted = i_procName; else; partLen = %int((LENGTH - %len(DELIMITERS)) / 2); formatted = %subst(i_procName: 1: partLen) + '....' + %subst(i_procName: %len(i_procName) - partLen + 1); endif;
formatted = ' ' + formatted + '() '; %len(formatted) = LENGTH + 4;
return formatted;
/END-FREE P E *

*=====================================================================* * SrvPgm: LOG4RPG - Object: Layout * *=====================================================================* * Author : Thomas Raddatz * * Date : 13.06.2006 * * E-mail : thomas.raddatz@tools400.de * * Homepage: www.tools400.de * *=====================================================================* * * * This software is free software, you can redistribute it and/or * * modify it under the terms of the GNU General Public License (GPL) * * as published by the Free Software Foundation. * * * * See GNU General Public License for details. * * http://www.opensource.org * * http://www.opensource.org/licenses/gpl-license.html * * * *=====================================================================* * History: * * * * Datum Name nderung * * ---------- ------------ --------------------------------------- * * 27.10.2006 Th.Raddatz Removed return value from procedure * * Layout_format(). * * * *=====================================================================* * >>PRE-COMPILER<< * * * * >>CRTCMD<< CRTRPGMOD MODULE(&LI/&OB) + * * SRCFILE(&SL/&SF) + * * SRCMBR(&SM); * * * * >>COMPILE<< * * >>PARM<< TRUNCNBR(*NO); * * >>PARM<< DBGVIEW(*LIST); * * >>PARM<< TGTRLS(V6R1M0 ); * * >>END-COMPILE<< * * * * >>EXECUTE<< * * * * >>END-PRE-COMPILER<< * *=====================================================================* /COPY QLOG4RPG,H_SPEC *=====================================================================* * * ----------------------------------------- * Exported functions * ----------------------------------------- /COPY QLOG4RPG,PLOG4RPG11 * * ----------------------------------------- * Type definitions * ----------------------------------------- D handle_t... D DS qualified based(pDummy) D layoutImpl likeds(LOG4RPG_classLayout_t ) * * ----------------------------------------- * Prototypes * ----------------------------------------- /COPY QLOG4RPG,CLIB C Library /COPY QLOG4RPG,CEELIB CEE-Procedures /COPY QBASICS1,PBASICS1 Basic RPG Extensions * /COPY QLOG4RPG,PLOG4RPG07 PropertyList /COPY QLOG4RPG,PLOG4RPG10 LogLog /COPY QLOG4RPG,PLOG4RPG12 ClassLoader /COPY QLOG4RPG,PLOG4RPG99 Helpers * /DEFINE DEFINE_PROCPTR /COPY QLOG4RPG,PLAYOUT Layout implementation /UNDEFINE DEFINE_PROCPTR * * ----------------------------------------- * Private prototypes * ----------------------------------------- * * ----------------------------------------- * Global constants * ----------------------------------------- * * ----------------------------------------- * Global fields * ----------------------------------------- * *========================================================================= * Creates a new layout. *========================================================================= * Parameters: * i_layoutImpl - A string that specifies the layout implementation * object. * i_pPropString - A string that specifies the properties for the * layout implementation object. * * Returns: * pLayout - Returns a handle to the layout object on success, * else a NULL handle. *========================================================================= P Layout_new... P B export * D Layout_new... D PI like(LOG4RPG_pLayout_t ) D i_layoutImpl const like(LOG4RPG_implClass_t ) D i_pPropString * const options(*nopass: D *omit : *string) * * Return value D layout DS likeds(handle_t ) D based(pLayout) * * Paremeter positions D p_pPropString C const(2) * * Fields for optional parameters D pPropString S like(i_pPropString ) inz * * Helper fields D isPresent S 10I 0 inz D pPropList S like(LOG4RPG_pPropertyList_t ) inz D tempLayout DS likeds(handle_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
LogLog_debug('Creating layout ...');
pLayout = Layout_null();
// Get optional parameters ... // ... property string if %parms() >= p_pPropString; CEETSTA(isPresent: p_pPropString: *omit); else; isPresent = 0; endif;
if isPresent = 1; pPropString = i_pPropString; else; pPropString = *NULL; endif;
// Initialize layout; if not ClassLoader_resolveLayout(i_layoutImpl: tempLayout.layoutImpl); return pLayout; endif;
// Create properties list if pPropString <> *NULL; pPropList = PropertyList_new(pPropString); else; pPropList = PropertyList_null(); endif;
// Create layout handle pLayout = %alloc(%size(handle_t)); layout = tempLayout;
// Instanciate layout implementation object g_pLayout_new = layout.layoutImpl.procNew.procptr; SrvPgmLayout_new(pLayout : layout.layoutImpl.pHandle : pPropList : '');
// Destroy properties list if not PropertyList_isNull(pPropList); PropertyList_delete(pPropList); endif;
LogLog_verbose('+ Allocated memory for layout.');
return pLayout;
/END-FREE * P Layout_new... P E * *========================================================================= * Formats the given string according to the layout. *========================================================================= * Parameters: * i_pLayout - Handle of the layout returned by Layout_new(). * * Returns: * text - Returns the formatted text on success, else ''. *========================================================================= P Layout_format... P B export * D Layout_format... D PI D i_pLayout const like(LOG4RPG_pLayout_t ) D i_pLogEvent const like(LOG4RPG_pLogEvent_t ) * * Layout handle D layout DS likeds(handle_t ) D based(i_pLayout) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
LogLog_verbose('-> Formatting log event of logger [' + LogEvent_getLoggerName(i_pLogEvent) + ']...');
g_pLayout_format = layout.layoutImpl.procFormat.procptr; SrvPgmLayout_format(layout.layoutImpl.pHandle : i_pLogEvent);
return;
/END-FREE * P Layout_format... P E * *========================================================================= * Returns the header of the layout. (optional layout procedure) *========================================================================= * Parameters: * i_pLayout - Handle of the layout returned by Layout_new(). * * Returns: * header - Returns the header of the layout. *========================================================================= P Layout_getHeader... P B export * D Layout_getHeader... D PI like(LOG4RPG_header_t ) D i_pLayout const like(LOG4RPG_pLayout_t ) * * Return value D header S like(LOG4RPG_header_t ) inz * * Layout handle D layout DS likeds(handle_t ) D based(i_pLayout) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
g_pLayout_getHeader = layout.layoutImpl.procGetHeader.procptr;
if g_pLayout_getHeader <> *NULL; header = SrvPgmLayout_getHeader(layout.layoutImpl.pHandle); endif;
return header;
/END-FREE * P Layout_getHeader... P E * *========================================================================= * Returns the footer of the layout. (optional layout procedure) *========================================================================= * Parameters: * i_pLayout - Handle of the layout returned by Layout_new(). * * Returns: * footer - Returns the footer of the layout. *========================================================================= P Layout_getFooter... P B export * D Layout_getFooter... D PI like(LOG4RPG_footer_t ) D i_pLayout const like(LOG4RPG_pLayout_t ) * * Return value D footer S like(LOG4RPG_footer_t ) inz * * Layout handle D layout DS likeds(handle_t ) D based(i_pLayout) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
g_pLayout_getFooter = layout.layoutImpl.procGetFooter.procptr;
if g_pLayout_getFooter <> *NULL; footer = SrvPgmLayout_getFooter(layout.layoutImpl.pHandle); endif;
return footer;
/END-FREE * P Layout_getFooter... P E * *========================================================================= * Returns the new-line character. *========================================================================= * Parameters: * void * * Returns: * newLine - The new line charcater of the system. *========================================================================= P Layout_newLine... P B export * D Layout_newLine... D PI 2A varying * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return '\n';
/END-FREE * P Layout_newLine... P E * *========================================================================= * Sets the properties of the specified layout. *========================================================================= * Parameters: * i_pLayout - Handle of the layout returned by Layout_new(). * i_pPropList - Handle of the properties list. * i_prefix - Prefix of the relevant property keys. * * Returns: * void *========================================================================= P Layout_setProperties... P B export * D Layout_setProperties... D PI D i_pLayout const like(LOG4RPG_pLayout_t ) D i_pPropList const like(LOG4RPG_pPropertyList_t ) D i_prefix const like(LOG4RPG_prefix_t ) * * Layout handle D layout DS likeds(handle_t ) D based(i_pLayout) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
g_pLayout_setProperties = layout.layoutImpl.procSetProperties.procptr; SrvPgmLayout_setProperties(layout.layoutImpl.pHandle : i_pPropList : i_prefix);
return;
/END-FREE * P Layout_setProperties... P E * *========================================================================= * Deletes the specified layout. *========================================================================= * Parameters: * io_pLayout - Handle of the layout returned by Layout_new(). * * Returns: * isDeleted - Returns TRUE on success, else FALSE. *========================================================================= P Layout_delete... P B export * D Layout_delete... D PI N D io_pLayout like(LOG4RPG_pLayout_t ) * * Return value D isDeleted S N inz(cFalse) * * Layout handle D layout DS likeds(handle_t ) D based(io_pLayout) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
LogLog_verbose('Deleting layout ...');
// Delete layout g_pLayout_delete = layout.layoutImpl.procDelete.procptr; isDeleted = SrvPgmLayout_delete(layout.layoutImpl.pHandle);
dealloc(N) io_pLayout; io_pLayout = Layout_null();
LogLog_verbose('- Free memory of layout.');
return isDeleted;
/END-FREE * P Layout_delete... P E * *========================================================================= * Tests, whether the specified property is known by the * super Layout. *========================================================================= * Parameters: * i_parameter - Parameter that is tested. * * Returns: * isKnown - Returns TRUE if the property is known by the super * Layout "class", else FALSE. *========================================================================= P Layout_knowsProperty... P B export * D Layout_knowsProperty... D PI N D i_parameter const like(LOG4RPG_Property_key_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
select; other; return cFalse; endsl;
/END-FREE * P Layout_knowsProperty... P E * *========================================================================= * Tests the specified layout for a NULL value. *========================================================================= * Parameters: * i_pLayout - Handle of the property list returned by * Layout_new(). * * Returns: * isNull - Returns TRUE if the handle of the layout is a * NULL handle. *========================================================================= P Layout_isNull... P B export * D Layout_isNull... D PI N D i_pLayout const like(LOG4RPG_pLayout_t ) * * Return value D isNull S N inz(cFalse) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
if i_pLayout = Layout_null(); isNull = cTrue; else; isNull = cFalse; endif;
return isNull;
/END-FREE * P Layout_isNull... P E * *========================================================================= * Returns a NULL layout. *========================================================================= * Parameters: * void * * Returns: * pLayout - Layout NULL handle. *========================================================================= P Layout_null... P B export * D Layout_null... D PI like(LOG4RPG_pLayout_t ) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
return *NULL;
/END-FREE * P Layout_null... P E *

*=====================================================================* * SrvPgm: LOG4RPG - Object: ClassLoader * *=====================================================================* * Author : Thomas Raddatz * * Date : 13.06.2006 * * E-mail : thomas.raddatz@tools400.de * * Homepage: www.tools400.de * *=====================================================================* * * * This software is free software, you can redistribute it and/or * * modify it under the terms of the GNU General Public License (GPL) * * as published by the Free Software Foundation. * * * * See GNU General Public License for details. * * http://www.opensource.org * * http://www.opensource.org/licenses/gpl-license.html * * * *=====================================================================* * History: * * * * Datum Name nderung * * ---------- ------------ --------------------------------------- * * 18.10.2006 Th.Raddatz Introduced Filter to Log4rpg. * * * * 14.11.2006 Th.Raddatz Changed call to f_getProcPtrByName() * * to omit message parameter to avoid * * program crash on error. * * Now using LogLog_verbose() to log * * errors when Layout_getHeader() or * * Layout_getFooter() could not be resolved.* * * *=====================================================================* * >>PRE-COMPILER<< * * * * >>CRTCMD<< CRTRPGMOD MODULE(&LI/&OB) + * * SRCFILE(&SL/&SF) + * * SRCMBR(&SM); * * * * >>COMPILE<< * * >>PARM<< TRUNCNBR(*NO); * * >>PARM<< DBGVIEW(*LIST); * * >>PARM<< TGTRLS(V6R1M0 ); * * >>END-COMPILE<< * * * * >>EXECUTE<< * * * * >>END-PRE-COMPILER<< * *=====================================================================* /COPY QLOG4RPG,H_SPEC *=====================================================================* * * ----------------------------------------- * Exported functions * ----------------------------------------- /COPY QLOG4RPG,PLOG4RPG12 * * ----------------------------------------- * Type definitions * ----------------------------------------- * * ----------------------------------------- * Prototypes * ----------------------------------------- /COPY QBASICS1,PBASICS1 Basic RPG Extensions * /COPY QLOG4RPG,PLOG4RPG10 LogLog /COPY QLOG4RPG,PLOG4RPG99 Helpers * * ----------------------------------------- * Private prototypes * ----------------------------------------- * * ----------------------------------------- * Global constants * ----------------------------------------- * Appender: D cAPPENDER_NEW... D C const('_new') D cAPPENDER_DOAPPEND... D C const('_doAppend') D cAPPENDER_REQUIRES_LAYOUT... D C const('_requiresLayout') D cAPPENDER_SET_PROPERTIES... D C const('_setProperties') D cAPPENDER_DELETE... D C const('_delete') * * Layout: D cLAYOUT_NEW... D C const('_new') D cLAYOUT_FORMAT... D C const('_format') D cLAYOUT_SET_PROPERTIES... D C const('_setProperties') D cLAYOUT_DELETE... D C const('_delete') D cLAYOUT_GET_HEADER... D C const('_getHeader') D cLAYOUT_GET_FOOTER... D C const('_getFooter') * * Filter: D cFILTER_NEW... D C const('_new') D cFILTER_DECIDE... D C const('_decide') D cFILTER_SET_PROPERTIES... D C const('_setProperties') D cFILTER_DELETE... D C const('_delete') * * ----------------------------------------- * Global fields * ----------------------------------------- * * ----------------------------------------- * Program status data structure * ----------------------------------------- /DEFINE SDS_EXTENDED /COPY QLOG4RPG,SDS /UNDEFINE SDS_EXTENDED * *========================================================================= * Resolves the service program that provides the implementation * of an appender. *========================================================================= * Parameters: * i_appendImpl - A string that specifies the appender implementation * object. * o_classAppender - The "class" that implements the appender. * * Returns: * isResolved - Returns TRUE on success, else FALSE. *========================================================================= P ClassLoader_resolveAppender... P B export * D ClassLoader_resolveAppender... D PI N D i_appendImpl const like(LOG4RPG_Property_value_t ) D o_classAppender... D likeds(LOG4RPG_classAppender_t) * * Return value D isResolved S N inz(cFalse) * * Helper fields D srvPgm DS likeds(qObj_t ) inz D proc S like(LOG4RPG_procedure_t ) inz D tempClassAppender... D DS likeds(LOG4RPG_classAppender_t) inz D msg DS likeds(msg_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clear o_classAppender; clear tempClassAppender;
srvPgm = getSrvPgm(i_appendImpl); proc = getProcedure(i_appendImpl);
tempClassAppender.pHandle = *NULL; tempClassAppender.srvPgm = f_activateSrvPgm(srvPgm.name : srvPgm.lib : msg); if msg.ID <> cMSG_ID_OK; LogLog_warning('Can not activate service program [' + i_appendImpl + ']'); return isResolved; endif;
tempClassAppender.proc = proc;
tempClassAppender.procNew = f_getProcPtrByName(proc + cAPPENDER_NEW : tempClassAppender.srvPgm: *omit); if tempClassAppender.procNew.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cAPPENDER_NEW); return isResolved; endif;
tempClassAppender.procRequiresLayout = f_getProcPtrByName(proc + cAPPENDER_REQUIRES_LAYOUT : tempClassAppender.srvPgm: *omit); if tempClassAppender.procRequiresLayout.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cAPPENDER_REQUIRES_LAYOUT); return isResolved; endif;
tempClassAppender.procDoAppend = f_getProcPtrByName(proc + cAPPENDER_DOAPPEND : tempClassAppender.srvPgm: *omit); if tempClassAppender.procDoAppend.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cAPPENDER_DOAPPEND); return isResolved; endif;
tempClassAppender.procSetProperties = f_getProcPtrByName(proc + cAPPENDER_SET_PROPERTIES : tempClassAppender.srvPgm: *omit); if tempClassAppender.procSetProperties.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cAPPENDER_SET_PROPERTIES); return isResolved; endif;
tempClassAppender.procDelete = f_getProcPtrByName(proc + cAPPENDER_DELETE : tempClassAppender.srvPgm: *omit); if tempClassAppender.procDelete.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cAPPENDER_DELETE); return isResolved; endif;
o_classAppender = tempClassAppender;
isResolved = cTrue;
return isResolved;
/END-FREE * P ClassLoader_resolveAppender... P E * *========================================================================= * Resolves the service program that provides the implementation * of a layout. *========================================================================= * Parameters: * i_layoutImpl - A string that specifies the layout implementation * object. * o_classLayout - The "class" that implements the layout. * * Returns: * isResolved - Returns TRUE on success, else FALSE. *========================================================================= P ClassLoader_resolveLayout... P B export * D ClassLoader_resolveLayout... D PI N D i_layoutImpl const like(LOG4RPG_Property_value_t ) D o_classLayout likeds(LOG4RPG_classLayout_t ) * * Return value D isResolved S N inz(cFalse) * * Helper fields D srvPgm DS likeds(qObj_t ) inz D proc S like(LOG4RPG_procedure_t ) inz D tempClassLayout... D DS likeds(LOG4RPG_classLayout_t ) inz D msg DS likeds(msg_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clear o_classLayout; clear tempClassLayout;
srvPgm = getSrvPgm(i_layoutImpl); proc = getProcedure(i_layoutImpl);
tempClassLayout.pHandle = *NULL; tempClassLayout.srvPgm = f_activateSrvPgm(srvPgm.name : srvPgm.lib : msg); if msg.ID <> cMSG_ID_OK; LogLog_warning('Can not activate service program [' + i_layoutImpl + ']'); return isResolved; endif;
tempClassLayout.proc = proc;
tempClassLayout.procNew = f_getProcPtrByName(proc + cLAYOUT_NEW : tempClassLayout.srvPgm: *omit); if tempClassLayout.procNew.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cLAYOUT_NEW); return isResolved; endif;
tempClassLayout.procFormat = f_getProcPtrByName(proc + cLAYOUT_FORMAT : tempClassLayout.srvPgm: *omit); if tempClassLayout.procFormat.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cLAYOUT_NEW); return isResolved; endif;
tempClassLayout.procSetProperties = f_getProcPtrByName(proc + cLAYOUT_SET_PROPERTIES : tempClassLayout.srvPgm: *omit); if tempClassLayout.procSetProperties.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cLAYOUT_NEW); return isResolved; endif;
tempClassLayout.procDelete = f_getProcPtrByName(proc + cLAYOUT_DELETE : tempClassLayout.srvPgm: *omit); if tempClassLayout.procDelete.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cLAYOUT_NEW); return isResolved; endif;
// Resolve optional procedures tempClassLayout.procGetHeader = f_getProcPtrByName(proc + cLAYOUT_GET_HEADER : tempClassLayout.srvPgm: *omit); if tempClassLayout.procGetHeader.procptr = *NULL; LogLog_verbose('Could not resolve procedure: ' + proc + cLAYOUT_GET_HEADER); endif;
tempClassLayout.procGetFooter = f_getProcPtrByName(proc + cLAYOUT_GET_FOOTER : tempClassLayout.srvPgm: *omit); if tempClassLayout.procGetFooter.procptr = *NULL; LogLog_verbose('Could not resolve procedure: ' + proc + cLAYOUT_GET_FOOTER); endif;
o_classLayout = tempClassLayout;
isResolved = cTrue;
return isResolved;
/END-FREE * P ClassLoader_resolveLayout... P E * *========================================================================= * Resolves the service program that provides the implementation * of a filter. *========================================================================= * Parameters: * i_filterImpl - A string that specifies the filter implementation * object. * o_classFilter - The "class" that implements the filter. * * Returns: * isResolved - Returns TRUE on success, else FALSE. *========================================================================= P ClassLoader_resolveFilter... P B export * D ClassLoader_resolveFilter... D PI N D i_filterImpl const like(LOG4RPG_Property_value_t ) D o_classFilter likeds(LOG4RPG_classFilter_t ) * * Return value D isResolved S N inz(cFalse) * * Helper fields D srvPgm DS likeds(qObj_t ) inz D proc S like(LOG4RPG_procedure_t ) inz D tempClassFilter... D DS likeds(LOG4RPG_classFilter_t ) inz D msg DS likeds(msg_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
clear o_classFilter; clear tempClassFilter;
srvPgm = getSrvPgm(i_filterImpl); proc = getProcedure(i_filterImpl);
tempClassFilter.pHandle = *NULL; tempClassFilter.srvPgm = f_activateSrvPgm(srvPgm.name : srvPgm.lib : msg); if msg.ID <> cMSG_ID_OK; LogLog_warning('Can not activate service program [' + i_filterImpl + ']'); return isResolved; endif;
tempClassFilter.proc = proc;
tempClassFilter.procNew = f_getProcPtrByName(proc + cFILTER_NEW : tempClassFilter.srvPgm: *omit); if tempClassFilter.procNew.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cFILTER_NEW); return isResolved; endif;
tempClassFilter.procDecide = f_getProcPtrByName(proc + cFILTER_DECIDE : tempClassFilter.srvPgm: *omit); if tempClassFilter.procDecide.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cFILTER_DECIDE); return isResolved; endif;
tempClassFilter.procSetProperties = f_getProcPtrByName(proc + cFILTER_SET_PROPERTIES : tempClassFilter.srvPgm: *omit); if tempClassFilter.procSetProperties.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cFILTER_SET_PROPERTIES); return isResolved; endif;
tempClassFilter.procDelete = f_getProcPtrByName(proc + cFILTER_DELETE : tempClassFilter.srvPgm: *omit); if tempClassFilter.procDelete.procptr = *NULL; LogLog_error('Could not resolve procedure: ' + proc + cFILTER_DELETE); return isResolved; endif;
o_classFilter = tempClassFilter;
isResolved = cTrue;
return isResolved;
/END-FREE * P ClassLoader_resolveFilter... P E *

