clear tempHandle;
// Initialize handle tempHandle.pSuper = i_pSuper; // Handle of the super class 'Layout' tempHandle.pattern = '%z [%M.%F] %-5p %S - %m';
// Initialize handle from configuration properties if (i_pPropList <> *NULL) and not PropertyList_isNull(i_pPropList); loadConfiguration(tempHandle: i_pPropList: i_prefix); endif;
io_pHandle = %alloc(%size(tempHandle)); handle = tempHandle;
LogLog_debug('Loaded instance of [' + cTHIS_CLASS + '].');
return isDone;
/END-FREE * P PatternLayout_new... P E * *========================================================================= * Formats a logging event and returns the result as a string. *========================================================================= * Parameters: * i_pHandle - Handle that is passed around the various calls to * this PatternLayout. * i_pLogEvent - Handle of the Log event returned by LogEvent_new(). * text of the log event that is formated. * * Returns: * text - Returns the formatted text of the log event. *========================================================================= * * Pattern variables: * ------------------ * %p priority * %t time * %d date * %z timestamp * %l logger name * %m application supplied message text * %n new line * %P program name * %L program library name * %M module name * %F function/procedure name * %S statement * %j full qualified job name * %u user name * %U current user name * * Sample pattern layout: * ---------------------- * * Layout: * '%z [%M.%F] %-5p %S - %m' * * Result: * 2006-02-22-11.38.42.567000 [LOG4RPG_T1.P_MAIN] debug 0000010500 - Hello World * * Description: * '%z' produces the current timestamp. * '%M' and '%F' prints the module and procedure name the logger was called from. * '%-5p' prints the logging priority, left adjusted in a 5 character space. * '%S' prints the statement identifier the logger was called from. * '%m' prints message text supplied by the application. * *========================================================================= P PatternLayout_format... P B export * D PatternLayout_format... D PI D i_pHandle * const D i_pLogEvent const like(LOG4RPG_pLogEvent_t ) * * Helper fields D x S 10I 0 inz D y S 10I 0 inz D lengthStr S 5A varying inz D length S 10I 0 inz D pattern S 1A inz D tmpText S like(LOG4RPG_formattedText_t) inz D string S like(LOG4RPG_formattedText_t) inz D caller DS likeds(LOG4RPG_caller_t ) inz D jobi0600 DS likeds(jobi0600_t ) inz * * Constants D cTMPTEXTMAXLEN C const(128) * * PatternLayout handle D handle DS likeds(handle_t ) based(i_pHandle) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Load return message text with pattern layout string = handle.pattern;
// Search for patterns and replace them x = %scan('%': string: 1); dow x > 0 and x < %len(string);
lengthStr = ''; length = 0; pattern = '';
// get length if specified y = x + 1; if (%subst(string: y: 1) = '-') and (y < %len(string)); y = y + 1; dow (y <= %len(string) and (%check('0123456789': %subst(string: y: 1)) = 0)); lengthStr = lengthStr + %subst(string: y: 1); y = y + 1; enddo; endif;
// get pattern pattern = %subst(string: y: 1);
// get length length = atoi(lengthStr);
// replace pattern select; when pattern = 'p'; // priority/level tmpText = Level_toString(LogEvent_getPriority(i_pLogEvent)); when pattern = 't'; // Time tmpText = %char(%time(LogEvent_getTime(i_pLogEvent)): *jobrun); when pattern = 'd'; // Date tmpText = %char(%date(LogEvent_getTime(i_pLogEvent)): *jobrun); when pattern = 'z'; // Timestamp tmpText = %char(LogEvent_getTime(i_pLogEvent)); when pattern = 'l'; // Logger Name tmpText = LogEvent_getLoggerName(i_pLogEvent); when pattern = 'm'; // application supplied message tmpText = LogEvent_getText(i_pLogEvent); when pattern = 'n'; // New Line tmpText = '\n'; when pattern = 'P'; // Program Name caller = LogEvent_getCaller(i_pLogEvent); tmpText = %trimR(caller.pgm.name); when pattern = 'L'; // Program Library Name tmpText = %trimR(LogEvent_resolveCallerPgmLibrary(i_pLogEvent)); when pattern = 'M'; // Module Name caller = LogEvent_getCaller(i_pLogEvent); tmpText = %trimR(caller.module.name); when pattern = 'F'; // Procedure/Function Name caller = LogEvent_getCaller(i_pLogEvent); tmpText = %trimR(caller.procedure); when pattern = 'S'; // Statement ID caller = LogEvent_getCaller(i_pLogEvent); tmpText = %trim(caller.stmtID); when pattern = 'j'; // full qualified job name tmpText = %editc(sds.nbr: 'X') + '/' + %trimR(sds.user) + '/' + %trimR(sds.job ); when pattern = 'u'; // user name tmpText = %trimR(sds.user); when pattern = 'U'; // current user name jobi0600 = rtvJobInf(); tmpText = jobi0600.curUser; other; tmpText = ''; endsl;
if pattern <> ''; if (length > 0) and (length < cTMPTEXTMAXLEN); %len(tmpText) = length; endif;
string = %replace(tmpText: string: x: y-x+1); endif;
if x < %len(string); x = %scan('%': string: y); else; x = 0; endif;
enddo;
LogEvent_setText(i_pLogEvent: string);
return;
/END-FREE * P PatternLayout_format... P E * *=====================================================================* * Used to set the properties of the layout. *=====================================================================* * Parameters: * i_pHandle - Handle that is passed around the various calls to * this PatternLayout. * i_pPropsList - Handle of the property list that contains the * properties for the appender. * i_prefix - Prefix of the relevant property keys. * * Returns: * isDone - Returns TRUE on success, else FALSE. *=====================================================================* P PatternLayout_setProperties... P B export * D PatternLayout_setProperties... D PI D i_pHandle * const D i_pPropList const like(LOG4RPG_pPropertyList_t ) D i_prefix const like(LOG4RPG_prefix_t ) * * PatternLayout handle D handle DS likeds(handle_t ) based(i_pHandle) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
loadConfiguration(handle: i_pPropList: i_prefix);
return;
/END-FREE * P PatternLayout_setProperties... P E * *=====================================================================* * Deletes a given PatternLayout. *=====================================================================* * Parameters: * io_pHandle - Handle that is passed around the various calls to * this PatternLayout. * * Returns: * void *=====================================================================* P PatternLayout_delete... P B export * D PatternLayout_delete... D PI N D io_pHandle * * * Return value D isDone S N inz(cFalse) * * PatternLayout handle D handle DS likeds(handle_t ) based(io_pHandle) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Clear handle data clear handle;
// Free handle memory dealloc(N) io_pHandle;
LogLog_verbose('Deleted instance of [' + cTHIS_CLASS + '].');
isDone = cTrue;
return isDone;
/END-FREE * P PatternLayout_delete... P E * *========================================================================= R * *** Private *** * Loads the configuration parameters from the specified property list. *========================================================================= P loadConfiguration... P B * D loadConfiguration... D PI D io_handle likeds(handle_t ) D i_pPropList const like(LOG4RPG_pPropertyList_t ) D i_prefix const like(LOG4RPG_prefix_t ) * * Local fields D parameter S like(property.key ) inz D prefix S like(i_prefix ) inz D property DS likeds(LOG4RPG_property_t) inz D propIter DS likeds(LOG4RPG_Property... D ListIterator_t) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
prefix = f_lcase(i_prefix);
propIter = PropertyList_getIterator(i_pPropList);
property = PropertyList_getFirst(propIter);
dow property.key <> '';
if (prefix = '') or (f_startsWith(prefix: property.key) and %len(property.key) > %len(prefix));
parameter = f_lcase(%subst(property.key: %len(prefix) + 1));
select; when parameter = cPARAMETER_CONVERSION_PATTERN; io_handle.pattern = property.value; when Layout_knowsProperty(parameter); // Ignore all parameters known by super Layout class other; LogLog_warning('No such property [' + property.key + '] in [' + cTHIS_CLASS + ']'); endsl;
endif;
property = PropertyList_getNext(propIter); enddo;
return;
/END-FREE * P loadConfiguration... P E * *========================================================================= R * *** Private *** * Retrieves job information into format jobi0600. *========================================================================= P rtvJobInf... P B * D rtvJobInf... D PI likeds(jobi0600_t ) * * Return value D jobi0600 DS likeds(jobi0600_t ) inz * * Local fields D qJob DS likeds(qJob_t ) inz D errCode DS likeds(errCode_t ) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
qjob.name = '*'; qjob.user = ''; qjob.nbr = ''; errCode = f_newApiErrCode(cTrue); QUSRJOBI(jobi0600: %size(jobi0600): 'JOBI0600': qJob: '': errCode);
return jobi0600;
/END-FREE * P rtvJobInf... P E *

