xmlString = '' + Layout_newLine();
return xmlString;
/END-FREE * P XMLLayout_getFooter... P E * *=====================================================================* * Used to set the properties of the layout. *=====================================================================* * Parameters: * i_pHandle - Handle that is passed around the various calls to * this XMLLayout. * i_pPropsList - Handle of the property list that contains the * properties for the appender. * i_prefix - Prefix of the relevant property keys. * * Returns: * isDone - Returns TRUE on success, else FALSE. *=====================================================================* P XMLLayout_setProperties... P B export * D XMLLayout_setProperties... D PI D i_pHandle * const D i_pPropList const like(LOG4RPG_pPropertyList_t ) D i_prefix const like(LOG4RPG_prefix_t ) * * XMLLayout handle D handle DS likeds(handle_t ) based(i_pHandle) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
loadConfiguration(handle: i_pPropList: i_prefix);
return;
/END-FREE * P XMLLayout_setProperties... P E * *=====================================================================* * Deletes a given XMLLayout. *=====================================================================* * Parameters: * io_pHandle - Handle that is passed around the various calls to * this XMLLayout. * * Returns: * void *=====================================================================* P XMLLayout_delete... P B export * D XMLLayout_delete... D PI N D io_pHandle * * * Return value D isDone S N inz(cFalse) * * XMLLayout handle D handle DS likeds(handle_t ) based(io_pHandle) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Clear handle data clear handle;
// Free handle memory dealloc(N) io_pHandle;
LogLog_verbose('Deleted instance of [' + cTHIS_CLASS + '].');
isDone = cTrue;
return isDone;
/END-FREE * P XMLLayout_delete... P E * *========================================================================= R * *** Private *** * Loads the configuration parameters from the specified property list. *========================================================================= P loadConfiguration... P B * D loadConfiguration... D PI D io_handle likeds(handle_t ) D i_pPropList const like(LOG4RPG_pPropertyList_t ) D i_prefix const like(LOG4RPG_prefix_t ) * * Local fields D parameter S like(property.key ) inz D prefix S like(i_prefix ) inz D property DS likeds(LOG4RPG_property_t) inz D propIter DS likeds(LOG4RPG_Property... D ListIterator_t) inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
prefix = f_lcase(i_prefix);
propIter = PropertyList_getIterator(i_pPropList);
property = PropertyList_getFirst(propIter);
dow property.key <> '';
if (prefix = '') or (f_startsWith(prefix: property.key) and B03 %len(property.key) > %len(prefix));
parameter = f_lcase(%subst(property.key: %len(prefix) + 1));
select; when parameter = cPARAMETER_REPLACE_UNPRINTABLE_CHARACTERS; if property.value = '1' or f_lcase(property.value) = 'true'; io_handle.rplUnprtChars = cTrue; else; io_handle.rplUnprtChars = cFalse; endif; when parameter = cPARAMETER_SUBSTITUTION_CHARACTER; io_handle.substitutionChar = property.value; when parameter = cPARAMETER_ENCODING; io_handle.encoding = property.value; when Layout_knowsProperty(parameter); // Ignore all parameters known by super Layout class other; LogLog_warning('No such property [' + property.key + '] in [' + cTHIS_CLASS + ']'); endsl;
endif;
property = PropertyList_getNext(propIter); enddo;
return;
/END-FREE * P loadConfiguration... P E * *=====================================================================* R * *** private *** * Entfernt nicht darstellbare Zeichen *=====================================================================* P rplcUnprtChars... P B export * D rplcUnprtChars... D PI like(LOG4RPG_formattedText_t) D i_handle const likeds(handle_t ) D i_text const like(LOG4RPG_formattedText_t) * * Return value D text1 S like(LOG4RPG_formattedText_t) inz D text2 S like(LOG4RPG_formattedText_t) inz * * Arbeitsfelder D x S 10I 0 inz * D END_CDATA S 3C inz(u'005D005D003E') ]]> D ESC_END_CDATA S 6C inz(u'005D005D002600670074003B') ]]> * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
// Replace closing string of CDATA section text1 = f_replace(%char(END_CDATA): %char(ESC_END_CDATA): i_text);
// Replace unprintable characters if not i_handle.rplUnprtChars; return text1; endif;
for x = 1 to %len(text1); if %subst(text1: x: 1) < x'40' or %subst(text1: x: 1) > x'FE'; text2 = text2 + i_handle.substitutionChar; else; text2 = text2 + %subst(text1: x: 1); endif; endfor;
return text2;
/END-FREE * P rplcUnprtChars... P E * ]]>

