P Synchronisation_setLogMode...
P                 E
 *
 *=========================================================================
 *  Sets the object description of the synchronisation object.
 *=========================================================================
P Synchronisation_setObjectDesc...
P                 B                   export
 *
D Synchronisation_setObjectDesc...
D                 PI
D  i_text                       50A   const  varying
 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
 /FREE

    g_objectDesc = i_text;

    return;

 /END-FREE
 *
P Synchronisation_setObjectDesc...
P                 E
 *
 *=========================================================================
 *  Start synchronisation by adding a lock on a given synchronisation
 *  data area.
 *=========================================================================
P Synchronisation_start...
P                 B                   export
 *
D Synchronisation_start...
D                 PI              N
D  i_dtaara                     10A   const
D  i_lib                        10A   const
D  o_ticket                                  likeds(LOG4RPG_syncTicket_t )
D  o_msg                                     likeds(msg_t )
D                                            options(*nopass: *omit)
 *
 *  Return value
D isStarted       S               N   inz(cFalse)
 *
 *  Parameter positions
D p_msg           C                   const(4)
 *
 *  Optional parameter fields
D msg             DS                  likeds(o_msg     ) inz
 *
 *  Helper fields
D isPresent       S             10I 0 inz
D cmd             S            128A   inz
D rcvm0200        DS                  likeds(rcvm0200_t) inz
 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
 /FREE

    clear o_ticket;
    msg = f_newMsg(cMSG_ID_OK);

    dou '1';

       if (not f_exist(i_dtaara: i_lib: '*DTAARA'));
          cmd = 'CRTDTAARA DTAARA(%LIB%/%DTAARA%) +
                           TYPE(*CHAR) LEN(%LEN%) +
                           TEXT(''' + g_objectDesc + ''')';
          cmd = f_replace('%LIB%'   : %trimR(i_lib)   : cmd);
          cmd = f_replace('%DTAARA%': %trimR(i_dtaara): cmd);
          cmd = f_replace('%LEN%'   : %char(%len(g_syncData)): cmd);
          f_execute(cmd: *omit: msg);
          if (msg.ID <> cMSG_ID_OK);
             leave;
          endif;
       endif;

       g_syncObjName = %trimR(i_lib) + '/' + %trimR(i_dtaara);

       monitor;
          in *lock g_syncData;
       on-error;
          rcvm0200 = f_rcvPgmMsg(cMsg_Escape: *omit: cMsg_Act_Old);
          msg = f_cvtRcvMsgToMsg(rcvm0200);
          leave;
       endmon;

       g_syncData.jobName = %trimR(%char(sds.nbr)) + '/' +
                            %trimR(sds.user)       + '/' +
                            %trimR(sds.job );
       g_syncData.alcTime = %timestamp();

       if (g_isLogMode);
          out *lock g_syncData;
       endif;

       o_ticket.jobName = g_syncData.jobName;
       o_ticket.alcTime = g_syncData.alcTime;
       isStarted = cTrue;
    enddo;

    if (msg.ID <> cMSG_ID_OK);
       msg.type = cMsg_Escape;
    endif;

    // Process optional/omissible message parameter
    if (%parms() >= p_msg);
       CEETSTA(isPresent: p_msg: *omit);
       if (isPresent = 1);
          o_msg = msg;
       endif;
    else;
       if (msg.ID <> cMSG_ID_OK);
          f_sndPgmMsg(msg: cMsg_Prv);
       endif;
    endif;

    return isStarted;

 /END-FREE
 *
P Synchronisation_start...
P                 E
 *
 *=========================================================================
 *  End synchronisation and release lock.
 *=========================================================================
P Synchronisation_end...
P                 B                   export
 *
D Synchronisation_end...
D                 PI
D  io_ticket                                 likeds(LOG4RPG_syncTicket_t )
D  o_msg                                     likeds(msg_t )
D                                            options(*nopass: *omit)
 *
 *  Parameter positions
D p_msg           C                   const(2)
 *
 *  Optional parameter fields
D msg             DS                  likeds(o_msg     ) inz
 *
 *  Helper fields
D isPresent       S             10I 0 inz
D rcvm0200        DS                  likeds(rcvm0200_t) inz
 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
 /FREE

    msg = f_newMsg(cMSG_ID_OK);

    dou '1';

       monitor;
          if (g_isLogMode);
             in *lock g_syncData;
          else;
             unlock g_syncData;
          endif;
       on-error;
          rcvm0200 = f_rcvPgmMsg(cMsg_Escape: *omit: cMsg_Act_Old);
          msg = f_cvtRcvMsgToMsg(rcvm0200);
          leave;
       endmon;

       if (g_isLogMode);
          p_assert(io_ticket.jobName = g_syncData.jobName and
                   io_ticket.alcTime = g_syncData.alcTime
                   : 'Invalid ticket data when attempting +
                      to release sync lock');

          clear g_syncData;
          out g_syncData;
       endif;

       clear io_ticket;
    enddo;

    if (msg.ID <> cMSG_ID_OK);
       msg.type = cMsg_Escape;
    endif;

    // Process optional/omissible message parameter
    if (%parms() >= p_msg);
       CEETSTA(isPresent: p_msg: *omit);
       if (isPresent = 1);
          o_msg = msg;
       endif;
    else;
       if (msg.ID <> cMSG_ID_OK);
          f_sndPgmMsg(msg: cMsg_Prv);
       endif;
    endif;

    return;

 /END-FREE
 *
P Synchronisation_end...
P                 E
 *

*=====================================================================* * SrvPgm: LOG4RPG - Object: Option Converter * *=====================================================================* * Author : Thomas Raddatz * * Date : 22.04.2008 * * E-mail : thomas.raddatz@tools400.de * * Homepage: www.tools400.de * *=====================================================================* * * * This software is free software, you can redistribute it and/or * * modify it under the terms of the GNU General Public License (GPL) * * as published by the Free Software Foundation. * * * * See GNU General Public License for details. * * http://www.opensource.org * * http://www.opensource.org/licenses/gpl-license.html * * * *=====================================================================* * History: * * * * Datum Name nderung * * ---------- ------------ --------------------------------------- * * * *=====================================================================* * >>PRE-COMPILER<< * * * * >>CRTCMD<< CRTRPGMOD MODULE(&LI/&OB) + * * SRCFILE(&SL/&SF) + * * SRCMBR(&SM); * * * * >>COMPILE<< * * >>PARM<< TRUNCNBR(*NO); * * >>PARM<< DBGVIEW(*LIST); * * >>PARM<< TGTRLS(V6R1M0 ); * * >>END-COMPILE<< * * * * >>EXECUTE<< * * * * >>END-PRE-COMPILER<< * *=====================================================================* /COPY QLOG4RPG,H_SPEC *=====================================================================* * * ----------------------------------------- * Exported functions * ----------------------------------------- /COPY QLOG4RPG,PLOG4RPG98 * * ----------------------------------------- * Type definitions * ----------------------------------------- * * ----------------------------------------- * Prototypes * ----------------------------------------- /COPY QLOG4RPG,CLIB C-Library /COPY QBASICS1,PBASICS1 Basic RPG Extensions /COPY QLOG4RPG,PLOG4RPG10 LogLog * * ----------------------------------------- * Private prototypes * ----------------------------------------- * * ----------------------------------------- * Global constants * ----------------------------------------- * * ----------------------------------------- * Global fields * ----------------------------------------- * * ----------------------------------------- * Program status data structure * ----------------------------------------- * *========================================================================= * Converts a given property to a file-size-value. *========================================================================= * Example: 512 -> 512 byte * 512KB -> 512 kByte * 512MB -> 512 MByte * 512GB -> 512 GByte *========================================================================= P OptionConverter_toFileSize... P B export * D OptionConverter_toFileSize... D PI 20I 0 D i_value value like(LOG4RPG_Property_value_t) D i_default 20I 0 value * * Return value D fileSize S 20I 0 inz * * Helper fields D string S like(i_value) D multiplier S 10I 0 inz D index S 10I 0 inz * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
string = %trim(i_value);
if (string = ''); return i_default; endif;
string = f_ucase(string); multiplier = 1;
dou '1';
index = %scan('KB': i_value); if (index <> 0); multiplier = 1024; string = %trimR(%subst(string: 1: index)); leave; endif;
index = %scan('MB': i_value); if (index <> 0); multiplier = 1024 * 1024; string = %trimR(%subst(string: 1: index)); leave; endif;
index = %scan('GB': i_value); if (index <> 0); multiplier = 1024 * 1024 * 1024; string = %trimR(%subst(string: 1: index)); leave; endif;
index = %check('0123456789 ': string); if (index > 0); LogLog_error('[' + string + '] is not in proper int form.'); LogLog_error('[' + i_value + '] not in expected format.'); endif;
enddo;
if (string = ''); return i_default; endif;
monitor; fileSize = atoi(string) * multiplier; on-error; LogLog_error('[' + string + '] is not in proper int form.'); LogLog_error('[' + i_value + '] not in expected format.'); endmon;
if (fileSize = 0); return i_default; endif;
return fileSize;
/END-FREE * P OptionConverter_toFileSize... P E * *========================================================================= * Converts a given property to a RPG boolean value. *========================================================================= * Example: 0 -> cFalse * 1 -> cTrue * false -> cFalse * true -> cTrue *========================================================================= P OptionConverter_toBoolean... P B export * D OptionConverter_toBoolean... D PI N D i_value value like(LOG4RPG_Property_value_t) D i_default N value * * Helper fields D string S like(i_value) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
string = %trim(i_value);
if (string = ''); return i_default; endif;
string = f_lcase(string);
select; when (string = '1' or string = 'true'); return cTrue; when (string = '0' or string = 'false'); return cFalse; endsl;
return i_default;
/END-FREE * P OptionConverter_toBoolean... P E * *========================================================================= * Converts a given property to a RPG integer value. *========================================================================= P OptionConverter_toInt... P B export * D OptionConverter_toInt... D PI 10I 0 D i_value value like(LOG4RPG_Property_value_t) D i_default 10I 0 value * * Helper fields D string S like(i_value) * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - /FREE
string = %trim(i_value);
if (string = ''); return i_default; endif;
return f_val(string);
/END-FREE * P OptionConverter_toInt... P E *

